const input = $input.first().json['Start variable'];

if (!input) {
  throw new Error("No GitHub URL provided in chat input.");
}

// Extract the GitHub URL (up to the repo name)
const urlRegex = /(https?:\/\/github\.com\/[^\/]+\/[^\/\s\{]+)/;
const urlMatch = input.match(urlRegex);

if (!urlMatch) {
  throw new Error("Invalid GitHub URL format. Expected format: https://github.com/owner/repo");
}

const repoUrl = urlMatch[1];

// Extract the owner and repo name
const repoRegex = /github\.com\/([^\/]+)\/([^\/\s]+)/;
const repoMatch = repoUrl.match(repoRegex);

if (!repoMatch) {
  throw new Error("Could not extract owner and repo from URL.");
}

// Extract number in curly braces, like {6}
const numberRegex = /\{(\d+)\}/;
const numberMatch = input.match(numberRegex);

return {
  owner: repoMatch[1],
  repo: repoMatch[2],
  repoUrl,
  number: numberMatch ? parseInt(numberMatch[1], 10) : null,
};

/**
 * This code processes an array of GitHub items (commits, issues, PRs)
 * and extracts relevant data. The patch_url is only added for commits.
 */

const results = $input.all().map(item => {
  // The output object is defined without patch_url initially.
  const output = {
    type: '',
    title: '',
    body: '',
    source: '',
  };

  const data = item.json;

  // Case 1: The item is a commit.
  if (data.commit && data.commit.message) {
    output.type = 'commit';
    const message = data.commit.message;
    const messageParts = message.split('\n\n');
    output.title = messageParts[0];
    output.body = messageParts.slice(1).join('\n\n').trim();
    output.source = data.html_url;

    // ADD the patch_url property ONLY if it's a commit.
    // We construct it from the commit's html_url.
    if (data.html_url) {
      output.patch_url = `${data.html_url}.patch`;
    }

  // Case 2: The item is an issue or pull request.
  } else if (data.title) {
    output.title = data.title;
    output.body = data.body || '';
    output.source = data.html_url;

    if (data.pull_request) {
      output.type = 'pull_request';
    } else {
      output.type = 'issue';
    }
    // The patch_url field is NOT added for issues or pull requests.
  }

  // Return the item if it's valid.
  if (output.type && output.title) {
    return { json: output };
  }

  return null;

}).filter(item => item !== null);

return results;

const results = $input.all().map(item => {
  const data = item.json;

  // Check if the item is a commit.
  if (data.type === 'commit' && data.source && data.title) {
    // Create a new object with only the desired fields.
    const output = {
      patch_url: `${data.source}.patch`
    };

    // Return the new, simplified commit object.
    return { json: output };
  }

  // For any non-commit items, return null so they will be filtered out.
  return null;

}).filter(item => item !== null); // This filters out all the null entries.

// Return the processed array, which now only contains commit data.
return results;

const patchItems = $input.all(0);
const allItems = $input.all(1);

// Build map: SHA ‚Üí patch content
const patchMap = new Map();

for (const patchItem of patchItems) {
  const patchContent = patchItem.json?.data;
  if (!patchContent) continue;

  const shaMatch = patchContent.match(/^From\s+([a-f0-9]{40})/m);
  if (shaMatch && shaMatch[1]) {
    const sha = shaMatch[1];
    patchMap.set(sha, patchContent);
  }
}

// Result array
const results = [];

for (const item of allItems) {
  const json = item.json;

  // Filter out raw patch-only items (typically have only `data`)
  if ('data' in json && !('type' in json)) continue;

  if (json.type === 'commit') {
    // Extract commit SHA from the `source` URL
    const sourceUrl = json.source || '';
    const shaMatch = sourceUrl.match(/\/commit\/([a-f0-9]{40})$/);
    const sha = shaMatch?.[1];

    if (sha && patchMap.has(sha)) {
      results.push({
        json: {
          ...json,
          patch: patchMap.get(sha),
          patch_url: undefined
        }
      });
      continue;
    }
  }

  // Pass all others (non-commit or unmatched commit) unchanged
  results.push(item);
}

return results;

const items = $input.all();

// Collect and stringify all JSON entries
const flatArray = items.map(item => item.json);
const stringifiedData = JSON.stringify(flatArray, null, 2); // Pretty format

return [
  {
    json: {
      data: stringifiedData
    }
  }
];

// n8n JavaScript Code - Team Flash Cards for GitHub Activity
// Parses the specific JSON structure and creates individual member cards

// Extract and parse the input data
const input = $input.all();
let teamMembers;

try {
  // Get the text content and extract JSON from the code block
  const textContent = input[0].json.text;
  const jsonMatch = textContent.match(/```json\n([\s\S]*?)\n```/);

  if (jsonMatch) {
    teamMembers = JSON.parse(jsonMatch[1]);
  } else {
    throw new Error("No JSON code block found in input");
  }
} catch (error) {
  return [{
    json: {
      error: "Failed to parse input: " + error.message,
      html_message: `<b>‚ùå Error parsing team data</b>\n<i>${error.message}</i>`,
      parse_mode: "HTML"
    }
  }];
}

// Helper function to format PR references
function highlightPRs(text) {
  return text.replace(/\(PR #(\d+)\)/g, '(<code>PR #$1</code>)');
}

// Helper function to truncate long text while preserving PR references
function smartTruncate(text, maxLength = 200) {
  if (text.length <= maxLength) return text;

  // Try to cut at a sentence boundary
  const truncated = text.substring(0, maxLength);
  const lastPeriod = truncated.lastIndexOf('.');
  const lastComma = truncated.lastIndexOf(',');

  if (lastPeriod > maxLength * 0.7) {
    return text.substring(0, lastPeriod + 1);
  } else if (lastComma > maxLength * 0.8) {
    return text.substring(0, lastComma) + '...';
  }

  return truncated + '...';
}

// Function to create individual member flash card
function createMemberFlashCard(member, cardIndex, totalCards) {
  const today = new Date().toLocaleDateString('en-US', { 
    weekday: 'short', 
    month: 'short', 
    day: 'numeric' 
  });

  // Card header
  let card = `<b>üë§ ${member.user}</b> <i>(${cardIndex}/${totalCards})</i>\n`;
  card += `<i>üìÖ ${today}</i>\n\n`;

  // Count activities for status indicator
  const accomplishedCount = member.accomplished?.length || 0;
  const ongoingCount = member.ongoing?.length || 0;
  const blockersCount = member.blockers?.length || 0;
  const nextStepsCount = member.next_steps?.length || 0;

  // Status indicator
  if (blockersCount > 0) {
    card += `üö® <b>ATTENTION NEEDED</b> - ${blockersCount} blocker${blockersCount > 1 ? 's' : ''}\n\n`;
  } else if (accomplishedCount + ongoingCount > 0) {
    card += `‚úÖ <b>ACTIVE</b> - ${accomplishedCount + ongoingCount} active item${(accomplishedCount + ongoingCount) > 1 ? 's' : ''}\n\n`;
  } else {
    card += `üò¥ <b>QUIET</b> - No recent activity\n\n`;
  }

  // ACCOMPLISHED WORK
  if (accomplishedCount > 0) {
    card += `<b>‚úÖ COMPLETED (${accomplishedCount})</b>\n`;
    member.accomplished.forEach((item, idx) => {
      const formattedItem = highlightPRs(smartTruncate(item, 180));
      card += `${idx + 1}. ${formattedItem}\n`;
    });
    card += `\n`;
  }

  // ONGOING WORK
  if (ongoingCount > 0) {
    card += `<b>üî® IN PROGRESS (${ongoingCount})</b>\n`;
    member.ongoing.forEach((item, idx) => {
      const formattedItem = highlightPRs(smartTruncate(item, 180));
      card += `${idx + 1}. ${formattedItem}\n`;
    });
    card += `\n`;
  }

  // BLOCKERS (highlighted)
  if (blockersCount > 0) {
    card += `<b>üö® BLOCKERS (${blockersCount})</b>\n`;
    member.blockers.forEach((item, idx) => {
      const formattedItem = smartTruncate(item, 180);
      card += `${idx + 1}. <i>${formattedItem}</i>\n`;
    });
    card += `\n`;
  }

  // NEXT STEPS
  if (nextStepsCount > 0) {
    card += `<b>üìã NEXT STEPS (${nextStepsCount})</b>\n`;
    member.next_steps.forEach((item, idx) => {
      const formattedItem = highlightPRs(smartTruncate(item, 180));
      card += `${idx + 1}. ${formattedItem}\n`;
    });
    card += `\n`;
  }

  // Quick summary footer
  const summaryParts = [];
  if (accomplishedCount > 0) summaryParts.push(`‚úÖ${accomplishedCount}`);
  if (ongoingCount > 0) summaryParts.push(`üî®${ongoingCount}`);
  if (blockersCount > 0) summaryParts.push(`üö®${blockersCount}`);
  if (nextStepsCount > 0) summaryParts.push(`üìã${nextStepsCount}`);

  if (summaryParts.length > 0) {
    card += `<b>üìä Summary:</b> ${summaryParts.join(' ‚Ä¢ ')}`;
  } else {
    card += `<i>No activity to report</i>`;
  }

  return card;
}

// Sort members - prioritize those with blockers, then by activity level
const sortedMembers = [...teamMembers].sort((a, b) => {
  const aBlockers = a.blockers?.length || 0;
  const bBlockers = b.blockers?.length || 0;

  // Blockers first
  if (aBlockers !== bBlockers) {
    return bBlockers - aBlockers;
  }

  // Then by total activity
  const aActivity = (a.accomplished?.length || 0) + (a.ongoing?.length || 0) + (a.next_steps?.length || 0);
  const bActivity = (b.accomplished?.length || 0) + (b.ongoing?.length || 0) + (b.next_steps?.length || 0);

  return bActivity - aActivity;
});

// Generate the flash cards
const flashCards = [];

// Create individual cards for each team member
sortedMembers.forEach((member, index) => {
  const accomplishedCount = member.accomplished?.length || 0;
  const ongoingCount = member.ongoing?.length || 0;
  const blockersCount = member.blockers?.length || 0;
  const nextStepsCount = member.next_steps?.length || 0;

  const totalActivity = accomplishedCount + ongoingCount + nextStepsCount;
  const hasBlockers = blockersCount > 0;

  flashCards.push({
    json: {
      message_type: "member_flash_card",
      member: member.user,
      html_message: createMemberFlashCard(member, index + 1, sortedMembers.length),
      parse_mode: "HTML",

      // Metadata for routing/filtering
      card_number: index + 1,
      total_cards: sortedMembers.length,
      has_blockers: hasBlockers,
      activity_level: hasBlockers ? "blocked" : totalActivity > 3 ? "high" : totalActivity > 1 ? "medium" : totalActivity > 0 ? "low" : "quiet",

      // Detailed counts
      accomplished_count: accomplishedCount,
      ongoing_count: ongoingCount,
      blockers_count: blockersCount,
      next_steps_count: nextStepsCount,
      total_activity: totalActivity,

      // Priority for notifications
      priority: hasBlockers ? "urgent" : totalActivity > 2 ? "high" : "normal"
    }
  });
});

return flashCards;
